(108, 'Develop Distinctive e-Channel Features and Branch Channel ‘Look and Feel’', 62, '4.5.C', 0, '2014-07-01', '2015-04-01', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:514px">\n	<tbody>\n		<tr>\n			<td style="height:38.25pt; width:386pt">\n			<p>Collaborate with EBG and Distribution Network to implement brand expressions across customer touch-points, i.e. e-channels, physical network, PBO/RM client treatment</p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(112, 'Align performance management structure to optimize lean branch', 74, '3.4.B', 0, NULL, NULL, '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:652px">\n	<tbody>\n		<tr>\n			<td style="height:38.25pt; width:489pt">-Significantly shifting micro CS service oriented KPI into a more sales oriented Sales generalist KPI<br />\n			-Develop Activity and sales monitoring system for new type CS<br />\n			-Align KPI of other staffs in micro distribution for Savings and cross sell of non-micro products</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
