(212, 'Upgrade Loyalty Platform (Fiestapoin)', 37, '5.7.D', 0, '2016-01-01', '2016-12-31', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:652px">\n	<tbody>\n		<tr>\n			<td style="height:25.5pt; width:489pt">Make changes to the loyalty platform such that users of all products and services can use the loyalty platform</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(213, 'Develop Pension Loan', 79, '4.13.A', 0, '2014-08-01', '2014-10-15', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:514px">\n	<tbody>\n		<tr>\n			<td style="height:51.0pt; width:386pt">\n			<ul>\n				<li>Design pension loan product (differentiated pricing for Mandiri pension account holders and outside), SOPs and SLAs</li>\n				<li>Seek risk approval from Retail Risk</li>\n				<li>Develop marketing plan to roll out product to Mandiri pension account holders</li>\n			</ul>\n			</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(214, 'Implement service catalogue and configuration management', 39, '5.8.A', 0, '2014-05-01', '2014-12-31', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:652px">\n	<tbody>\n		<tr>\n			<td style="height:25.5pt; width:489pt">List of IT services provided to business with description, SLA/ key metrics and pricing mechanism and IT assets information repository and management of changes and history through their lifecycle</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
