(216, 'Develop Healthcare top-up loan', 79, '4.13.B', 0, '2014-07-01', '2015-04-01', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:514px">\n	<tbody>\n		<tr>\n			<td style="height:38.25pt; width:386pt">\n			<ul>\n				<li>Design healthcare top-up financing product, SOPs and SLAs</li>\n				<li>Seek risk approval from Retail Risk</li>\n				<li>Develop marketing plan to roll out product via Healthcare sector strategies</li>\n			</ul>\n			</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(217, 'Kick-start loan takeover program', 79, '4.13.C', 0, '2014-08-01', '2015-05-01', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:514px">\n	<tbody>\n		<tr>\n			<td style="height:38.25pt; width:386pt">\n			<ul>\n				<li>Benchmark personal loan pricing and develop pricing structure for takeovers</li>\n				<li>Develop customer targeting plan (i.e. which bank&rsquo;s customers to target) and design specific takeover strategies</li>\n			</ul>\n\n			<p>&nbsp;</p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(218, 'Market expansion through Mandiri lean branch', 79, '4.13.D', 0, '2015-07-01', '2016-07-01', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:514px">\n	<tbody>\n		<tr>\n			<td style="height:51.0pt; width:386pt">\n			<ul>\n				<li>Collaborate with Micro and Distribution Network to develop a personal loan origination plan in lean branches</li>\n				<li>Plan should include target offers, marketing program, and roles of staff in sales and loan approvals</li>\n			</ul>\n			</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(219, 'Develop pre-approved credit card programs targeting M/SME/C customers ', 80, '4.14.A', 0, NULL, '2014-11-24', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:514px">\n	<tbody>\n		<tr>\n			<td style="height:63.75pt; width:386pt">\n			<ul>\n				<li>Finalize customer criteria, in collaboration with Micro and CBB</li>\n				<li>Design pre-approved offers according to target segment, for example Business Card Platinum for Commercial customers</li>\n				<li>Develop scoring system, RAC, SLAs, SOPs</li>\n				<li>Roll out program via direct marketing, in-branch and BBC/CBCs</li>\n			</ul>\n			</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(220, 'Develop pre-approved credit card programs targeting CASA balance holders and payroll accounts', 80, '4.14.B', 0, '2014-07-01', '2015-01-01', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:514px">\n	<tbody>\n		<tr>\n			<td style="height:51.0pt; width:386pt">\n			<ul>\n				<li>Develop customer criteria, in collaboration with Mass Banking, Micro and Priority Banking</li>\n				<li>Develop scoring system, RAC, SLAs, SOPs</li>\n				<li>Roll out program via Bank at Work program, direct marketing, and branches</li>\n			</ul>\n			</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(221, 'Develop pre-approved personal loan to payroll account holders', 80, '4.14.C', 0, '2014-07-01', '2014-07-01', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:514px">\n	<tbody>\n		<tr>\n			<td style="height:38.25pt; width:386pt">\n			<ul>\n				<li>Develop customer criteria, in collaboration with Mass Banking and Micro</li>\n				<li>Structure pricing system and limit setting based on customer segment</li>\n				<li>Roll out program via Bank at Work program, direct marketing, and branches</li>\n			</ul>\n			</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', '');
INSERT INTO `initiative` (`id`, `title`, `program_id`, `code`, `tier`, `start`, `end`, `kickoff`, `completion`, `description`, `GH_PIC`, `parent_code`, `status`) VALUES
(222, 'Improve underwriting process across consumer finance products ', 81, '4.15.A', 0, '2014-07-01', '2015-04-01', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:514px">\n	<tbody>\n		<tr>\n			<td style="height:114.75pt; width:386pt">\n			<ul>\n				<li>Develop differentiated underwriting process based on customer segment and origination channel</li>\n				<li>Ensure most applications will receive an alternative offer i.e. down-sell or up-sell if customer does not meet/exceed requirement for original limit size</li>\n				<li>Ensure document follow-up processes are pursued efficiently through improved communication channels with clear task ownership</li>\n				<li>Develop capabilities to monitor the absolute application process time</li>\n				<li>Utilize capabilities to underwrite customers based on CASA patterns</li>\n				<li>Improve TAT on application processing</li>\n			</ul>\n			</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(223, 'Install marketing engine to optimize credit card marketing campaign', 81, '4.15.B', 0, '2014-07-01', '2014-10-20', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:514px">\n	<tbody>\n		<tr>\n			<td style="height:76.5pt; width:386pt">\n			<ul>\n				<li>Create/buy tool to assist in identifying most suitable candidates for marketing campaigns</li>\n				<li>Utilize a more systematic and efficient method to test new ideas around optimizing portfolio</li>\n				<li>Test and Learn approach should be applied across credit card loyalty programs, rewards, card activation initiatives etc.</li>\n			</ul>\n			</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(224, 'Enhance credit card operating platform', 81, '4.15.C', 0, '2014-07-01', '2015-10-01', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:514px">\n	<tbody>\n		<tr>\n			<td style="height:38.25pt; width:386pt">\n			<ul>\n				<li>Integrate systems for better and complete customer information</li>\n				<li>Drive automation as part of business process improvement</li>\n				<li>Enhance credit card scoring system</li>\n			</ul>\n			</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(225, 'Establish strategic partnerships and incubate emerging and high potential e-Commerce / m-Commerce players (i.e. payment facilitators, marketplaces, digital apps)', 82, '4.16.A', 0, '2014-07-01', '2016-07-01', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:514px">\n	<tbody>\n		<tr>\n			<td style="height:76.5pt; width:386pt">\n			<ul>\n				<li>Commission a market study on e-/m-Commerce market to craft participation strategy</li>\n				<li>Develop list of promising e-/m-Commerce players and establish preliminary agenda, i.e. strategic partnerships, incubation and/or M&amp;A</li>\n				<li>Explore additional partnerships and activities (sponsorship of startup activity) to gain knowledge and access to startups</li>\n				<li>Follow through with strategic agenda</li>\n			</ul>\n			</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(227, 'Identify and close gaps in platform performance and features for mobile, internet and ATM platforms', 83, '4.17.A', 0, '2016-07-01', '2017-07-01', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:514px">\n	<tbody>\n		<tr>\n			<td style="height:102.0pt; width:386pt">\n			<ul>\n				<li>Perform gap assessment on current performance and features of mobile banking, internet banking and ATM</li>\n				<li>Benchmark to local (and global peers where applicable) and identify gaps in features and capabilities</li>\n				<li>Develop list of features (i.e., digital applications for P2P interactions and value-added services) and platform capabilities to initiate with gaps assessment as basis</li>\n				<li>Collaborate with IT and EBG to develop timeline and execution plan to incorporate new features</li>\n			</ul>\n			</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(228, '"Credit Analytics Resources (conditional on where it resides, with risk or business)"', 49, '7.1.A', 0, NULL, NULL, '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:652px">\n	<tbody>\n		<tr>\n			<td style="height:24.75pt; width:489pt">Build/transfer credit analytics resources (e.g. from existing corporate risk people).</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(229, '"Credit Analytics Capabilities (conditional on where it resides, with risk or business)"', 49, '7.1.B', 0, '2015-01-01', '2015-12-01', '', '7.1.C', '<table border="0" cellpadding="0" cellspacing="0" style="width:652px">\n	<tbody>\n		<tr>\n			<td style="height:29.25pt; width:489pt">Build credit analytics capabilities by training, workshop or benchmarking.</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(230, 'Industry Expertise Resources and Capabilities', 49, '7.1.C', 0, '2015-01-01', '2015-12-01', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:652px">\n	<tbody>\n		<tr>\n			<td style="height:22.5pt; width:489pt">Build industry expertise resources and develop capabilities to pair with RM on transactional and portfolio level (by training, workshop or benchmarking).</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(231, 'Credit Policy Enhancement', 49, '7.1.D', 0, '2015-01-01', '2015-12-01', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:652px">\n	<tbody>\n		<tr>\n			<td style="height:22.5pt; width:489pt">Modify and enhance credit policies to reflect changes in the credit process and credit risk management.</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(232, 'Rating Model for Specific Industry', 51, '7.2.A', 0, '2015-04-01', '2018-12-01', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:652px">\n	<tbody>\n		<tr>\n			<td style="height:22.5pt; width:489pt">Develop or fine tuning more granular rating models (and Basel II parameters) for corporate and commercial segment, capable of sectoral risk differentiation based on specific industry/sector. Pilot on Healthcare and Ports sectors, while full sectors roll out will be delivered subsequently until 2018.<br />\n			(dependent on the completion of Non-Retail Credit Risk stream of Basel II &amp; ERM Implementation project by KPMG)</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(233, 'Rating Model for SME (Business Banking) Limit Rp. 2-10 Billion (Corporate SME)', 51, '7.2.B', 0, '2015-04-01', '2015-12-01', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:652px">\n	<tbody>\n		<tr>\n			<td style="height:22.5pt; width:489pt">Develop rating model for SME (Business Banking) with limit Rp. 2-10 Billion whose characteristics are more to corporate (corporate SME) rather than small enterprise (business banking).</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(234, 'Rating/scorecard Mechanism or Policy for Group Rating', 51, '7.2.C', 0, '2015-04-01', '2016-12-01', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:652px">\n	<tbody>\n		<tr>\n			<td style="height:22.5pt; width:489pt">Build rating/scorecard mechanism or policy for Value Chain Group (Group Rating). Pilot on Healthcare and Ports sectors, while full sectors roll out will be delivered subsequently until 2016.<br />\n			(dependent on the completion of Non-Retail Credit Risk stream of Basel II &amp; ERM Implementation project by KPMG)</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(235, 'Integrated Spreadsheet', 51, '7.2.D', 0, '2015-03-01', '2015-12-01', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:652px">\n	<tbody>\n		<tr>\n			<td style="height:22.5pt; width:489pt">Develop industry integrated spreadsheet (as a group).<br />\n			Modify and enhance credit spreadsheet to reflet changes in the credit process and credit risk management.</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(236, 'Rating Models for Structured Finance and Capital Market Product', 51, '7.2.E', 0, '2015-04-01', '2017-12-01', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:652px">\n	<tbody>\n		<tr>\n			<td style="height:22.5pt; width:489pt">Build or fine tuning rating models (and Basel II risk parameters) for Project Finance (specialized lending) as well as Commodities Finance and Equities Finance (where applicable and supported by portfolio data).<br />\n			(dependent on the completion of Non-Retail Credit Risk stream of Basel II &amp; ERM Implementation project by KPMG)<br />\n			<br />\n			Notes: Structured Finance may include Project Finance, Asset Finance (ships, aircraft, etc.), Real Estate, Commodities, Export Finance (to be coordinated with the Business Unit)</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(237, 'Scoring Model for Distributor Financing', 51, '7.2.F', 0, '2015-01-01', '2015-12-01', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:652px">\n	<tbody>\n		<tr>\n			<td style="height:30px; width:652px">Build scoring model based on cash flow transaction (cash flow lending) for customer that need trust receipt (TR) and bank guarantee (BG) facility&nbsp; as part of supply chain solution.</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(238, 'Basel II Risk Parameters for Retail Exposures', 51, '7.2.G', 0, '2015-04-01', '2018-12-01', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:652px">\n	<tbody>\n		<tr>\n			<td style="height:22.5pt; width:489pt">Develop or enhance Basel II Risk Parameters (PD, LGD, EAD) for retail exposures under Basel II asset classification.</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(239, 'Risk Appetite for Priority Sectors', 54, '7.3.A', 0, '2015-01-01', '2015-12-01', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:652px">\n	<tbody>\n		<tr>\n			<td style="height:22.5pt; width:489pt">Build risk appetite for selected priority sectors (via Basel II &amp; ERM project by KPMG) with pilot on Healthcare and Ports.</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(240, 'Risk Process Alignment', 54, '7.3.B', 0, '2015-01-01', '2015-12-01', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:652px">\n	<tbody>\n		<tr>\n			<td style="height:22.5pt; width:489pt">Differentiate risk process to align with customer rating/ differentiation (platinum, gold, silver).<br />\n			Establish SLAs for each tier (to agree processing times, etc).</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(241, 'Value Chain Pilots (Supply Chain) for Healthcare and Ports Sector Solutions', 54, '7.3.C', 0, '2015-01-01', '2015-12-01', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:652px">\n	<tbody>\n		<tr>\n			<td style="height:22.5pt; width:489pt">Develop/enhance limit and exposure management (via ICLS project by Moody&rsquo;s), risk measurement tools and monitoring system for supply chain pilots (healthcare and ports sectors).</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(242, 'Value Chain Pilots (Distribution Chain) for Selected Sector Solutions', 54, '7.3.D', 0, '2015-01-01', '2015-12-01', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:652px">\n	<tbody>\n		<tr>\n			<td style="height:22.5pt; width:489pt">Develop/enhance limit and exposure management (via ICLS project by Moody&rsquo;s), risk measurement tools and monitoring system for supply chain pilots (healthcare and ports sectors).</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(243, 'Portfolio Guideline and Industry Classification', 54, '7.3.E', 0, '2015-01-01', '2015-12-01', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:652px">\n	<tbody>\n		<tr>\n			<td style="height:22.5pt; width:489pt">Refine or build Industry Classification, Industry Limit, Industry Acceptance Criteria and Portfolio Allocation/Optimization.</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(244, 'Risk-Based Pricing, Liabilities Pricing, and Pricing Authority', 54, '7.3.F', 0, '2015-01-01', '2016-12-01', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:652px">\n	<tbody>\n		<tr>\n			<td style="height:22.5pt; width:489pt">Develop or enhance Risk-Based Pricing model based on best practice approaches (e.g. expected loss, RAROC, RORWA; via ERM System by Oracle).<br />\n			Develop risk adjusted return metrics (KPIs for bankers)<br />\n			Develop Liabilities Pricing model based on best practice approaches which aligned with asset pricing.<br />\n			Design asset-liability pricing mechanism and authority for RM to optimize product offering and overall profitability.</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(245, 'Risk Analytics Alignment to SME Reconfiguration', 57, '7.4.A', 0, '2015-06-01', '2015-12-01', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:652px">\n	<tbody>\n		<tr>\n			<td style="height:22.5pt; width:489pt">Align/adjust monitoring tools to new SME boundary.<br />\n			Ensure consistency across segments, especially as customers transition (avoid opportunities for arbitrage).<br />\n			Facilitate smooth transition.</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
