(294, 'Review total rewards philosophy, purpose of each element and alignment with EVP', 48, '6.5.F', 0, '2014-07-14', '2014-11-14', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:652px">\n	<tbody>\n		<tr>\n			<td style="height:102px; width:652px">Evaluate current rewards philosophy in the bank to determine if it will still be able to support future business and talent strategy<br />\n			Examine the role of total rewards&nbsp; for the bank and group in the areas of foundational, performance and career rewards<br />\n			Determine the strategic purposes of each reward element in Mandiri</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(295, '"Conduct benefits review and consider implementing flexible benefits program (Design: Jan - March 2015  Rollout: Apr - Jun 2015)"', 48, '6.5.G', 0, '2015-01-15', '2015-05-31', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:652px">\n	<tbody>\n		<tr>\n			<td style="height:63.75pt; width:489pt">Review current benefits items<br />\n			Ensure current benefits have been de-linked from performance level (e.g. KKP) and is based on market prevalence<br />\n			Consider implementing a new flexible benefit scheme for the bank</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
(296, '"Review KPI setting process and develop revised KPI setting approach (Design: Jul- Sept 2014 Rollout: Oct - Dec 2014)"', 48, '6.5.H', 0, '2014-07-14', '2014-12-31', '', '', '<table border="0" cellpadding="0" cellspacing="0" style="width:652px">\n	<tbody>\n		<tr>\n			<td style="height:51.0pt; width:489pt">Review quantity &amp; quality of current KPIs set<br />\n			Review relevance of Balanced Scorecard framework<br />\n			Ensure no more than 6-8 KPIs for each role<br />\n			Ensure goals are locked once goal setting discussion is completed with manager</td>\n		</tr>\n	</tbody>\n</table>\n', 0, '0', ''),
